mapper.select:
SELECT * FROM (SELECT INNER_QUERY.*, ROWNUM AS rnum FROM (SELECT PCOM.POST_COMMENT_ID, PCOM.COMMUNITY_ID, PCOM.MEMBER_ID, PCOM.CONTENT, PCOM.CREATE_AT, PCOM.MODIFIED_AT, MEM.NICKNAME FROM POST_COMMENT PCOM INNER JOIN MEMBER MEM ON PCOM.MEMBER_ID = MEM.MEMBER_ID WHERE 1=1
AND (PCOM.CONTENT LIKE '%' || #{keyword} || '%' OR MEM.NICKNAME LIKE '%' || #{keyword} || '%')
ORDER BY PCOM.POST_COMMENT_ID DESC) INNER_QUERY WHERE ROWNUM <= #{endRow}) WHERE rnum >= #{startRow}

