mapper.select:
        SELECT * 
        FROM (
            SELECT ROWNUM AS RN, INNERTABLE.*
            FROM (
                SELECT MEMBER_ID, EMAIL, NICKNAME, RESUME_MAX, CREATE_AT, MODIFIED_AT, PROVIDER
                FROM MEMBER

						WHERE UPPER(EMAIL) LIKE UPPER('%' || #{keyword} || '%')
                		OR UPPER(NICKNAME) LIKE UPPER('%' || #{keyword} || '%')

               		ORDER BY MEMBER_ID DESC
            ) INNERTABLE
            WHERE ROWNUM <= #{endRow}
        )
        WHERE RN >= #{startRow}
