mapper.select:
SELECT * FROM (SELECT INNER_QUERY.*, ROWNUM AS rnum FROM (SELECT NOTI.NOTICE_ID, NOTI.ADMIN_ID, NOTI.NOTICE_CATEGORY_ID, NOTI.TITLE,NOTI.CONTENT, NOTI.CREATE_AT, NOTI.MODIFIED_AT, NOTI.VIEWS, CATE.NOTICE_CATEGORY_CONTENT FROM NOTICE NOTI INNER JOIN NOTICE_CATEGORY CATE ON NOTI.NOTICE_CATEGORY_ID = CATE.NOTICE_CATEGORY_ID WHERE 1=1
AND (NOTI.TITLE LIKE '%' || #{keyword} || '%' OR CATE.NOTICE_CATEGORY_CONTENT LIKE '%' || #{keyword} || '%')
ORDER BY NOTI.NOTICE_ID DESC) INNER_QUERY WHERE ROWNUM <= #{endRow}) WHERE rnum >= #{startRow}
