mapper.delete:
DELETE FROM COMPANY
WHERE COMPANY_ID IN

    #{companyId}

mapper.select:
SELECT COMPANY_ID, EMAIL, PASSWORD, COMPANY_NAME, BRN, REPRESENTATIVE, TEL, POSTAL_CODE_ID, ADDRESS
FROM COMPANY
WHERE EMAIL = #{email, jdbcType=VARCHAR}
AND PASSWORD = #{password, jdbcType=VARCHAR}

SELECT EMAIL
FROM COMPANY
WHERE REPRESENTATIVE = #{representative}
AND BRN = #{brn}
