mapper.select:
SELECT jp.job_post_id AS jobPostId, c.company_id AS companyId, c.company_name AS companyName, jp.title AS title, jp.career_type AS careerType, jp.education AS education, j.job_name AS jobName, jp.end_date AS endDate, jp.job_img AS jobImg FROM job_post jp JOIN company c ON jp.company_id = c.company_id JOIN job j ON jp.job_id = j.job_id
WHERE (UPPER(jp.TITLE) LIKE CONCAT('%', UPPER(#{keyword}), '%')) OR (LOWER(jp.TITLE) LIKE CONCAT('%', LOWER(#{keyword}), '%')) OR (UPPER(jp.CONTENT) LIKE CONCAT('%', UPPER(#{keyword}), '%')) OR (LOWER(jp.CONTENT) LIKE CONCAT('%', LOWER(#{keyword}), '%')) OR (UPPER(c.COMPANY_NAME) LIKE CONCAT('%', UPPER(#{keyword}), '%')) OR (LOWER(c.COMPANY_NAME) LIKE CONCAT('%', LOWER(#{keyword}), '%'))
ORDER BY jp.end_date, job_post_id ASC LIMIT #{startRow}, #{pageSize}
